mapper.select:

SELECT * FROM (
	SELECT ROWNUM AS rnum, subquery.*
	FROM (
		SELECT cu.users_name, r.reference_title, r.reference_created_date, r.reference_answered_yn
		FROM tbl_reference r
		JOIN tbl_users u ON r.reference_users_number = u.users_number
		JOIN tbl_care_users cu ON u.users_number = cu.users_number
		WHERE r.reference_users_number = #{referenceUsersNumber}
		ORDER BY r.reference_created_date DESC
	) subquery
) WHERE rnum BETWEEN #{startRow} AND #{endRow}
